package guvi;

public class ReverseString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String c= "Hello world";
    	String rc= " ";
    	char h;
    	for(int i=0; i<c.length(); i++){
    		{	
    		h = c.charAt(i);
    		rc= h+rc;
    		{
    	System.out.println("Reverse srting =  "  +rc);}
	}

}}}
